function gerarDataMais3Dias() {
  const hoje = new Date();
  const diaSeguinte = new Date(hoje);
  diaSeguinte.setDate(diaSeguinte.getDate() + 1);

  const resultado = new Date(diaSeguinte);
  resultado.setDate(resultado.getDate() + 3);

  const ano = resultado.getFullYear();
  const mes = String(resultado.getMonth() + 1).padStart(2, '0');
  const dia = String(resultado.getDate()).padStart(2, '0');

  return `${ano}-${mes}-${dia}`;
}

